mapper.select:
select count(*) from hiquality.hqt_checkorder


        and Id REGEXP #{hqtCheckorder.id}


        and OrderId REGEXP #{hqtCheckorder.orderid}


        and OrderName REGEXP #{hqtCheckorder.ordername}


        and OrderDate REGEXP DATE_FORMAT(#{hqtCheckorder.orderdate},'%y-%m-%d')


        and HopeEndDate REGEXP DATE_FORMAT(#{hqtCheckorder.hopeenddate},'%y-%m-%d')


        and FinishedDate REGEXP DATE_FORMAT(#{hqtCheckorder.finisheddate},'%y-%m-%d')


        and CheckerId REGEXP #{hqtCheckorder.checkerid}


        and CustOrderId REGEXP #{hqtCheckorder.custorderid}


        and Contactor REGEXP #{hqtCheckorder.contactor}


        and ReportExpTime REGEXP DATE_FORMAT(#{hqtCheckorder.reportexptime},'%y-%m-%d')


        and Amt REGEXP #{hqtCheckorder.amt}


        and PayAmt REGEXP #{hqtCheckorder.payamt}


        and PayStatus REGEXP #{hqtCheckorder.paystatus}


        and InvoiceStatus REGEXP #{hqtCheckorder.invoicestatus}


        and Status REGEXP #{hqtCheckorder.status}


        and InvoiceId REGEXP #{hqtCheckorder.invoiceid}


        and ReportId REGEXP #{hqtCheckorder.reportid}


        and ReportEndDate REGEXP DATE_FORMAT(#{hqtCheckorder.reportenddate},'%y-%m-%d')


        and ProcRecord REGEXP #{hqtCheckorder.procrecord}


        and Remark REGEXP #{hqtCheckorder.remark}


        and EdtByUserId REGEXP #{hqtCheckorder.edtbyuserid}


        and EdtTime REGEXP DATE_FORMAT(#{hqtCheckorder.edttime},'%y-%m-%d')


        and Sender REGEXP #{hqtCheckorder.sender}


        and TelNo REGEXP #{hqtCheckorder.telno}


        and CheckResult REGEXP #{hqtCheckorder.checkresult}


        and Accepter REGEXP #{hqtCheckorder.accepter}


        and PayRemark REGEXP #{hqtCheckorder.payremark}


        and InvoiceRemark REGEXP #{hqtCheckorder.invoiceremark}


        and PayDate REGEXP DATE_FORMAT(#{hqtCheckorder.paydate},'%y-%m-%d')


        and PayRecorder REGEXP #{hqtCheckorder.payrecorder}


        and InvoiceDate REGEXP DATE_FORMAT(#{hqtCheckorder.invoicedate},'%y-%m-%d')


        and InvoiceRecorder REGEXP #{hqtCheckorder.invoicerecorder}




                and ifnull(${key},'') in

                    #{valueitem}







                and ifnull(${key},'') between '${value.get(0)}' and
                '${value.get(1)}'

