mapper.select:
select count(*) from hiquality.hqt_customerorder


        and Id REGEXP #{hqtCustomerorder.id}


        and OrderId REGEXP #{hqtCustomerorder.orderid}


        and OrderName REGEXP #{hqtCustomerorder.ordername}


        and OrderDate REGEXP DATE_FORMAT(#{hqtCustomerorder.orderdate},'%y-%m-%d')


        and HopeEndDate REGEXP DATE_FORMAT(#{hqtCustomerorder.hopeenddate},'%y-%m-%d')


        and FinishedDate REGEXP DATE_FORMAT(#{hqtCustomerorder.finisheddate},'%y-%m-%d')


        and CustomerId REGEXP #{hqtCustomerorder.customerid}


        and SampleName REGEXP #{hqtCustomerorder.samplename}


        and Contactor REGEXP #{hqtCustomerorder.contactor}


        and CheckRequire REGEXP #{hqtCustomerorder.checkrequire}


        and Amt REGEXP #{hqtCustomerorder.amt}


        and InAmt REGEXP #{hqtCustomerorder.inamt}


        and InStatus REGEXP #{hqtCustomerorder.instatus}


        and InvoiceStatus REGEXP #{hqtCustomerorder.invoicestatus}


        and Status REGEXP #{hqtCustomerorder.status}


        and InvoiceId REGEXP #{hqtCustomerorder.invoiceid}


        and ProcRecord REGEXP #{hqtCustomerorder.procrecord}


        and Remark REGEXP #{hqtCustomerorder.remark}


        and EdtByUserId REGEXP #{hqtCustomerorder.edtbyuserid}


        and EdtTime REGEXP DATE_FORMAT(#{hqtCustomerorder.edttime},'%y-%m-%d')


        and ApplyOrderId REGEXP #{hqtCustomerorder.applyorderid}


        and ChargeMan REGEXP #{hqtCustomerorder.chargeman}


        and TelNo REGEXP #{hqtCustomerorder.telno}


        and OrderMan REGEXP #{hqtCustomerorder.orderman}


        and Checkers REGEXP #{hqtCustomerorder.checkers}


        and InRemark REGEXP #{hqtCustomerorder.inremark}


        and InvoiceRemark REGEXP #{hqtCustomerorder.invoiceremark}


        and InDate REGEXP DATE_FORMAT(#{hqtCustomerorder.indate},'%y-%m-%d')


        and InRecorder REGEXP #{hqtCustomerorder.inrecorder}


        and InvoiceDate REGEXP DATE_FORMAT(#{hqtCustomerorder.invoicedate},'%y-%m-%d')


        and InvoiceRecorder REGEXP #{hqtCustomerorder.invoicerecorder}




                and ifnull(${key},'') in

                    #{valueitem}







                and ifnull(${key},'') between '${value.get(0)}' and
                '${value.get(1)}'

